import React, { Component } from 'react';
import { ResponsiveLine } from '@nivo/line';

const data = [
    {
      "id": "RIWI UK data: Is it the right time to make a major purchase? (n = 27,737)",
      "data": [
          {"x":"18-11-2017", "y":"19.5"},
          {"x":"21-11-2017", "y":"20.25"},
          {"x":"02-12-2017", "y":"20"},
          {"x":"09-12-2017", "y":"19.75"},
          {"x":"16-12-2017", "y":"18.5"},
          {"x":"23-12-2017", "y":"21"},
          {"x":"30-12-2017", "y":"18.75"},
          {"x":"06-01-2018", "y":"17"},
          {"x":"13-01-2018", "y":"17"},
          {"x":"20-01-2018", "y":"20.25"},
          {"x":"27-01-2018", "y":"20"},
          {"x":"03-02-2018", "y":"19.75"},
          {"x":"10-02-2018", "y":"17"},
          {"x":"17-02-2018", "y":"16.5"},
          {"x":"24-02-2018", "y":"16"},
          {"x":"03-03-2018", "y":"17"},
          {"x":"10-03-2018", "y":"16"},
          {"x":"17-03-2018", "y":"17"},
          {"x":"24-03-2018", "y":"19"},
          {"x":"31-03-2018", "y":"20"},
          {"x":"07-04-2018", "y":"19.7"},
          {"x":"14-04-2018", "y":"15.5"},
          {"x":"21-04-2018", "y":"20"},
      ]
    },

  ]

const key = []

class LineGraph extends Component {
    render() {
        return (
            <div className="App">
                <header style={{fontSize: "21px"}}>Do Americans support current US approach to trade negotiations?</header>
                <div style={{height: 600}}>
                    <ResponsiveLine
                        data = {data}
                        curve = "monotoneY"
                        colors="spectral"
                        colorBy="id"
                        //indexBy="id"
                        keys={key}
                        margin={{
                            "top": 50,
                            "right": 60,
                            "bottom": 50,
                            "left": 60
                        }}
                        xScale={{
                            //"type": "point"
                            "type":"time",
                            "format":"%d-%m-%Y"
                        }}
                        yScale={{
                            "type": "linear",
                            "min": "15",
                            "max": "25"
                        }}
                        stacked={true}
                        axisBottom={{
                            "orient": "bottom",
                            "tickSize": 5,
                            "tickPadding": 13,
                            //"tickRotation":45,
                            //"legend": "q02_educated_risk / country_name",
                            "legendPosition": "middle",
                            "legendOffset": 40,
                            "format": '%d %b %y',
                        }}
                        axisLeft={{
                            "orient": "center",
                            "tickSize": 5,
                            "tickPadding": 13,
                            "tickRotation": 0,
                            //"legend": "(%)",
                            "legendPosition": "middle",
                            "legendOffset": -40,
                            //"format": 'not sure how to fix this'
                        }}
                        //enableGridY={false}
                        enableGridX={false}
                        lineWidth={3}
                        dotSize={6}
                        dotColor="inherit:darker(0.3)"
                        dotBorderWidth={0}
                        dotBorderColor="inherit"
                        enableDotLabel={true}
                        dotLabel = {
                            d=>`${d.y}%`
                        }
                        dotLabelColor="#000000"
                        dotLabelYOffset={-9}
                        //enableArea={true}
                        areaOpacity={0.1}
                        animate={true}
                        motionStiffness={90}
                        motionDamping={15}
                        isInteractive={false}
                        legends={[
                            {
                                "anchor": "top",
                                "direction": "row",
                                "justify": false,
                                "itemWidth": 1,
                                "itemHeight": 15,
                                "itemDirection": "left-to-right",
                            }
                        ]}
                        theme={{
                            dotLabel: {text:{fontSize: '13px', fontWeight: 'bold'}},
                            legends: {text:{fontSize: '13px'}},
                            axis: {legend: {text:{fontSize: '13px',fontWeight: 'bold'}}, ticks: {text:{fontSize: '13px'}},},
                        }}
                    />
                </div>
            </div>
        )
    }
}

export default LineGraph;
