import React, { Component } from 'react';
import { ResponsivePie } from '@nivo/pie';

const data = [
  {
    "id": "Yes",
    "value": 35,
  },
  {
    "id": "No",
    "value": 27,
  },
  {
    "id": "Don't Know",
    "value": 38,
  },
]

const colors = ['#4d676d','#618189','#749AA5','#85b0bc','#96c6d3','#96d1cc','#96d1bc','#86baa7','#77a393','#64877a','#506b60','#4e6865']

class DonutGraph extends Component {
        render() {
            return (
                <div className="App">
                    <header style={{fontSize: "30px"}}>Do Americans support current US approach to trade negotiations?</header>
                    <div style={{height: 600}}>
                        <ResponsivePie
                            data = {data}
                            innerRadius={0.45}
                            colors={colors}
                            colorBy="id"
                            margin={{
                                top: 80,
                                right: 120,
                                bottom: 80,
                                left: 120
                            }}
                            sliceLabel = {
                                d=>`${d.value}%`
                            }
                            slicesLabelsSkipAngle={0}
                            slicesLabelsTextColor="#96c6d3"
                            borderWidth={0}
                            padAngle={0.5}
                            cornerRadius={5}
                            radialLabel = 'id'
                            radialLabelsLinkColor="inherit"
                            radialLabelsLinkStrokeWidth={3}
                            radialLabelsTextColor="inherit:darker(1)"
                            isInteractive={false}
                            theme={{
                                sliceLabel: {text:{fontSize: '19px'}},
                            }}
                            animate={true}
                            motionStiffness={90}
                            motionDamping={15}
                        />
                    </div>
                </div>
            );
        }
}

export default DonutGraph;
