import React, { Component } from 'react';
import './App.css';
import { ResponsiveBar } from '@nivo/bar';

const data = [
    {
      "value": "Yes", 
      "percent":35,
    },
    {
      "value": "No",
      "percent": 27,
    },
    {
      "value": "Don't Know", 
      "percent": 38,
    },
  ]

const colors = ['#4d676d','#618189','#749AA5','#85b0bc','#96c6d3','#96d1cc','#96d1bc','#86baa7','#77a393','#64877a','#506b60','#4e6865']

class App extends Component {
  render() {
    return (
      <div className="App">
        <header style={{fontSize: "30px"}}>Do Americans support current US approach to trade negotiations?</header>
        <div style={{height: 600}}>
          <ResponsiveBar
            data = {data}
            minValue={0}
            maxValue={100}
            colors={colors}
            colorBy="id"
            borderRadius={2}
            indexBy="value"
            keys={[
              "percent"
            ]}
            margin={{
              "top": 50,
              "right": 130,
              "bottom": 50,
              "left": 60
            }}
            padding={0.3}
            groupMode="grouped"
            borderWidth={1}
            borderColor="inherit:darker(0.3)"
            axisBottom={{
                "orient": "bottom",
                "tickSize": 5,
                "tickPadding": 13,
                "tickRotation": 0,
                "legend": "(%)",
                "legendPosition": "middle",
                "legendOffset": 40,
            }}
            axisLeft={{
                "orient": "center",
                "tickSize": 5,
                "tickPadding": 13,
                "tickRotation": -90,
                //"legend": "q02_educated_risk / country_name",
                "legendPosition": "middle",
                "legendOffset": -40,
            }}
            legends={[
              {
                "dataFrom": "keys",
                "anchor": "top-right",
                "direction": "column",
                "justify": false,
                "itemWidth": 1,
                "itemHeight": 15,
                "itemDirection": "left-to-right"
              }
            ]}
            //layout="vetical"
            //enableGridY={false}
            labelTextColor="inherit:brighter(1.6)"
            //label={false}
            animate={true}
            motionStiffness={90}
            motionDamping={15}
            tooltip
            theme={{
              labels: {text:{fontSize: '19px'}},
              legends: {text:{fontSize: '19px'}},
              axis: {legend: {text:{fontSize: '19px',fontWeight: 'bold'}}, ticks: {text:{fontSize: '19px'}},},
              tooltip: {container: {background: '#333', color: '#fff', fontSize: '19px'}},
            }}
          />
        </div>
      </div>
    );
  }
}

export default App;
