import React, { Component } from 'react';
import './App.css';
import { ResponsiveBar } from '@nivo/bar';

const data = [
    {
      "value": "very_educated", 
      "BR": 53,
      "DE": 31,
      "IN": 51,
      "NG": 66,
      "PH": 46,
      "RU": 35,
      "ZA": 65,
      "UA": 28,
      "UK": 35,
      "US": 43,
    },
    {
      "value": "somewhat_educated",
      "BR": 28,
      "DE": 24,
      "IN": 21,
      "NG": 15,
      "PH": 28,
      "RU": 34,
      "ZA": 17,
      "UA": 48,
      "UK": 31,
      "US": 24,
    },
    {
      "value": "not_too_educated", 
      "BR": 7,
      "DE": 18,
      "IN": 10,
      "NG": 11,
      "PH": 12,
      "RU": 15,
      "ZA": 9,
      "UA": 16,
      "UK": 18,
      "US": 14,
    },
    {
      "value": "not_at_all_educated", 
      "BR": 12,
      "DE": 27,
      "IN": 18,
      "NG": 8,
      "PH": 13,
      "RU": 17,
      "ZA": 9,
      "UA": 8,
      "UK": 16,
      "US": 20,
    },
  ]

const colors = ['#394d51','#4d676d','#618189','#749AA5','#85b0bc','#96c6d3']

class App extends Component {
  render() {
    return (
      <div className="App" style={{height: 900}}>
        <ResponsiveBar
          data = {data}
          //height={900}
          //width={900}
          colors={colors}
          colorBy="id"
          borderRadius={2}
          indexBy="value"
          keys={[
            "BR",
            "DE",
            "IN",
            "NG",
            "PH",
            "RU",
            "ZA",
            "UA",
            "UK",
            "US",
          ]}
          margin={{
            "top": 50,
            "right": 130,
            "bottom": 50,
            "left": 60
          }}
          padding={0.3}
          groupMode="grouped"
          layout="horizontal"
          borderWidth={1}
          borderColor="inherit:darker(0.3)"
          axisBottom={{
              "orient": "bottom",
              "tickSize": 5,
              "tickPadding": 5,
              "tickRotation": 0,
              "legend": "country",
              "legendPosition": "middle",
              "legendOffset": 36
          }}
          axisLeft={{
              "orient": "left",
              "tickSize": 5,
              "tickPadding": 5,
              "tickRotation": 0,
              "legend": "food",
              "legendPosition": "middle",
              "legendOffset": -40
          }}
          //enableGridY={false}
          labelTextColor="inherit:brighter(1.6)"
          //label={false}
          animate={true}
          motionStiffness={90}
          motionDamping={15}
          theme={{tooltip: {container: {background: '#333'}}}}
        />
      </div>
    );
  }
}

export default App;
