import React, { Component } from 'react';
import { ResponsiveLine } from '@nivo/line';

const data = [
    {
      "id": "RIWI UK data: Is it the right time to make a major purchase? (n = 27,737)",
      "data": [
          {"x":"18-Nov-17", "y":"19.5"},
          {"x":"2-Dec-17", "y":"20"},
          {"x":"16-Dec-17", "y":"18.5"},
          {"x":"30-Dec-17", "y":"18.75"},
          {"x":"13-Jan-18", "y":"17"},
          {"x":"27-Jan-18", "y":"20"},
          {"x":"10-Feb-18", "y":"17"},
          {"x":"24-Feb-18", "y":"16"},
          {"x":"10-Mar-18", "y":"16"},
          {"x":"24-Mar-18", "y":"19"},
          {"x":"7-Apr-18", "y":"19.7"},
          {"x":"21-Apr-18", "y":"20"},
      ]
    },
    {
        "id": "John Lewis Department store UK sales, % change from previous year",
        "data": [
            {"x":"18-Nov-17", "y":"19"},
            {"x":"2-Dec-17", "y":"19.5"},
            {"x":"16-Dec-17", "y":"18.5"},
            {"x":"30-Dec-17", "y":"18.5"},
            {"x":"13-Jan-18", "y":"17.5"},
            {"x":"27-Jan-18", "y":"20"},
            {"x":"10-Feb-18", "y":"19"},
            {"x":"24-Feb-18", "y":"19"},
            {"x":"10-Mar-18", "y":"21"},
            {"x":"24-Mar-18", "y":"19.5"},
            {"x":"7-Apr-18", "y":"19.75"},
            {"x":"21-Apr-18", "y":"19.5"},
        ]
      },
  ]

const key = []

class LineGraph extends Component {
    render() {
        return (
            <div className="App">
                <header style={{fontSize: "21px"}}>Do Americans support current US approach to trade negotiations?</header>
                <div style={{height: 600}}>
                    <ResponsiveLine
                        data = {data}
                        curve = "monotoneY"
                        minY={auto}
                        maxY={auto}
                        colors="spectral"
                        colorBy="id"
                        //indexBy="id"
                        keys={key}
                        margin={{
                            "top": 50,
                            "right": 130,
                            "bottom": 50,
                            "left": 60
                        }}
                        xScale={{
                            "type": "point"
                        }}
                        yScale={{
                            "type": "linear",
                            "stacked": true,
                            "min": "auto",
                            "max": "auto"
                        }}
                        stacked={true}
                        borderWidth={1}
                        borderColor="inherit:darker(0.3)"
                        axisBottom={{
                            "orient": "bottom",
                            "tickSize": 5,
                            "tickPadding": 13,
                            "tickRotation": 0,
                            //"legend": "q02_educated_risk / country_name",
                            "legendPosition": "middle",
                            "legendOffset": 40,
                        }}
                        axisLeft={{
                            "orient": "center",
                            "tickSize": 5,
                            "tickPadding": 13,
                            "tickRotation": -90,
                            //"legend": "(%)",
                            "legendPosition": "middle",
                            "legendOffset": -40,
                        }}
                        dotSize={6}
                        dotColor="inherit:darker(0.3)"
                        dotBorderWidth={0}
                        dotBorderColor="inherit"
                        enableDotLabel={true}
                        dotLabel = {
                            d=>`${d.y}%`
                        }
                        dotLabelColor="inherit:darker(3.1)"
                        dotLabelYOffset={-9}
                        animate={true}
                        motionStiffness={90}
                        motionDamping={15}
                        legends={[
                            {
                              "anchor": "top-right",
                              "direction": "column",
                              "justify": false,
                              "itemWidth": 1,
                              "itemHeight": 15,
                              "itemDirection": "left-to-right"
                            }
                        ]}
                    />
                </div>
            </div>
        )
    }
}

export default LineGraph;
