import React, { Component } from 'react';
import { ResponsivePie } from '@nivo/pie';

const data = [
    {
      "value": "Yes", 
      "percent":35,
    },
    {
      "value": "No",
      "percent": 27,
    },
    {
      "value": "Don't Know", 
      "percent": 38,
    },
  ]

  const colors = ['#4d676d','#618189','#749AA5','#85b0bc','#96c6d3','#96d1cc','#96d1bc','#86baa7','#77a393','#64877a','#506b60','#4e6865']

  class DonutGraph extends Component {
    render() {
        return (
          <div className="App">
            <header style={{fontSize: "30px"}}>Do Americans support current US approach to trade negotiations?</header>
            <div style={{height: 900}}>
              <ResponsivePie
                data = {data}
                colors={colors}
                colorBy="id"
                innerRadius={0.5}
                padAngle={0.7}
                cornerRadius={3}
                indexBy="value"
                margin={{
                    "top": 40,
                    "right": 80,
                    "bottom": 80,
                    "left": 80
                }}
                borderWidth={1}
                borderColor="inherit:darker(0.2)"
                radialLabelsSkipAngle={10}
                radialLabelsTextXOffset={6}
                radialLabelsTextColor="#333333"
                radialLabelsLinkOffset={0}
                radialLabelsLinkDiagonalLength={16}
                radialLabelsLinkHorizontalLength={24}
                radialLabelsLinkStrokeWidth={1}
                radialLabelsLinkColor="inherit"
                slicesLabelsSkipAngle={10}
                slicesLabelsTextColor="#333333"
                padding={0.3}
                borderWidth={1}
                borderColor="inherit:darker(0.3)"
                legends={[
                  {
                    "dataFrom": "keys",
                    "anchor": "top-right",
                    "direction": "column",
                    "justify": false,
                    "itemWidth": 1,
                    "itemHeight": 15,
                    "itemDirection": "left-to-right"
                  }
                ]}
                label = {
                    d=>`${d.value}%`
                }
                labelTextColor="inherit:brighter(1.6)"
                //label={false}
                animate={true}
                motionStiffness={90}
                motionDamping={15}
                isInteractive={false}
                theme={{
                  //labels: {text:{fontSize: '19px'}},
                  //legends: {text:{fontSize: '19px'}},
                  tooltip: {container: {background: '#333', color: '#fff', fontSize: '19px'}},
                }}
              />
            </div>
          </div>
        );
      }
    }

export default DonutGraph;
