import React, { Component } from 'react';
import './App.css';
import { Bar } from '@nivo/bar';

const data = [
    {
      "value": "very_educated", 
      "percent": 45,
    },
    {
      "value": "somewhat_educated",
      "percent": 26,
    },
    {
      "value": "not_too_educated", 
      "percent": 13,
    },
    {
      "value": "not_at_all_educated", 
      "percent": 15,
    },
  ]

class App extends Component {
  render() {
    return (
      <div className="App">
        <Bar
          data = {data}
          height={900}
          width={900}
          //colors="spectral"
          colorBy="id"
          borderRadius={2}
          indexBy="value"
          keys={[
            "percent",
          ]}
          margin={{
            "top": 50,
            "right": 130,
            "bottom": 50,
            "left": 60
          }}
          padding={0.3}
          groupMode="grouped"
          borderWidth={1}
          borderColor="inherit:darker(0.3)"
          axisBottom={{
              "orient": "bottom",
              "tickSize": 5,
              "tickPadding": 5,
              "tickRotation": 0,
              "legend": "country",
              "legendPosition": "center",
              "legendOffset": 36
          }}
          axisLeft={{
              "orient": "left",
              "tickSize": 5,
              "tickPadding": 5,
              "tickRotation": 0,
              "legend": "food",
              "legendPosition": "center",
              "legendOffset": -40
          }}
          //labelSkipWidth={12}
          //labelSkipHeight={12}
          //labelTextColor="inherit:darker(1.6)"
          animate={true}
          motionStiffness={90}
          motionDamping={15}
          //tooltipFormat={}
        />
      </div>
    );
  }
}

export default App;
