import React, { Component } from 'react';
import './App.css';
import { ResponsiveBar } from '@nivo/bar';

const data = [
    {
      "value": "very_educated", 
      "BR": 61,
      "DE": 31,
      "IN": 53,
    },
    {
      "value": "somewhat_educated",
      "BR": 16,
      "DE": 21,
      "IN": 10,
    },
    {
      "value": "not_too_educated", 
      "BR": 10,
      "DE": 18,
      "IN": 11,
    },
    {
      "value": "not_at_all_educated", 
      "BR": 13,
      "DE": 30,
      "IN": 27,
    },
  ]

const colors = ['#394d51','#4d676d']

class App extends Component {
  render() {
    return (
      <div className="App" style={{height: 900}}>
        <ResponsiveBar
          data = {data}
          //height={900}
          //width={900}
          colors={colors}
          colorBy="id"
          borderRadius={2}
          indexBy="value"
          keys={[
            "BR",
            "DE",
            "IN",
          ]}
          margin={{
            "top": 50,
            "right": 130,
            "bottom": 50,
            "left": 60
          }}
          padding={0.3}
          groupMode="grouped"
          layout="horizontal"
          borderWidth={1}
          borderColor="inherit:darker(0.3)"
          axisBottom={{
              "orient": "bottom",
              "tickSize": 5,
              "tickPadding": 5,
              "tickRotation": 0,
              "legend": "country",
              "legendPosition": "middle",
              "legendOffset": 36
          }}
          axisLeft={{
              "orient": "left",
              "tickSize": 5,
              "tickPadding": 5,
              "tickRotation": 0,
              "legend": "food",
              "legendPosition": "middle",
              "legendOffset": -40
          }}
          //labelSkipWidth={12}
          //labelSkipHeight={12}
          labelTextColor="inherit:darker(1.6)"
          //label={false}
          animate={true}
          motionStiffness={90}
          motionDamping={15}
        />
      </div>
    );
  }
}

export default App;
