import React, { Component } from 'react';
import { ResponsivePie } from '@nivo/pie';

const data = [
    {
      "value": "Yes", 
      "percent":35,
    },
    {
      "value": "No",
      "percent": 27,
    },
    {
      "value": "Don't Know", 
      "percent": 38,
    },
  ]

  const colors = ['#4d676d','#618189','#749AA5','#85b0bc','#96c6d3','#96d1cc','#96d1bc','#86baa7','#77a393','#64877a','#506b60','#4e6865']

  class DonutGraph extends Component {
    render() {
        return (
          <div className="App">
            <header style={{fontSize: "30px"}}>Do Americans support current US approach to trade negotiations?</header>
            <div style={{height: 600}}>
              <ResponsivePie
                data = {data}
                colors={colors}
                colorBy="id"
                innerRadius={0.5}
                padAngle={0.7}
                cornerRadius={3}
                indexBy="value"
                margin={{
                  "top": 50,
                  "right": 130,
                  "bottom": 50,
                  "left": 60
                }}
                padding={0.3}
                borderWidth={1}
                borderColor="inherit:darker(0.3)"
                legends={[
                  {
                    "dataFrom": "keys",
                    "anchor": "top-right",
                    "direction": "column",
                    "justify": false,
                    "itemWidth": 1,
                    "itemHeight": 15,
                    "itemDirection": "left-to-right"
                  }
                ]}
                label = {
                    d=>`${d.value}%`
                }
                labelTextColor="inherit:brighter(1.6)"
                //label={false}
                animate={true}
                motionStiffness={90}
                motionDamping={15}
                isInteractive={false}
                theme={{
                  labels: {text:{fontSize: '19px'}},
                  legends: {text:{fontSize: '19px'}},
                  tooltip: {container: {background: '#333', color: '#fff', fontSize: '19px'}},
                }}
              />
            </div>
          </div>
        );
      }
    }

export default DonutGraph;
